import React from "react";
import { motion } from "framer-motion";

export default function Contacts() {
  return (
    <motion.div
      className="relative font-sans px-2 sm:px-6 md:px-8 max-w-6xl mx-auto"
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
    >
      {/* Изображение — приподнято выше основного контента */}
      <motion.img
        src="/media/avatar.jpg"
        alt="Visual Work"
        className="w-full max-w-4xl object-cover rounded-xl shadow-xl mb-10 -mt-28 md:-mt-36 lg:-mt-44"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.7, ease: "easeOut", delay: 0.2 }}
      />

      {/* Текстовая часть — справа */}
      <motion.div
        className="md:flex md:justify-end"
        initial={{ opacity: 0, x: 20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5, ease: "easeOut", delay: 0.4 }}
      >
        <div className="text-lg md:text-xl max-w-xl text-right leading-relaxed text-gray-900 dark:text-gray-100 space-y-6">
          <p className="text-2xl md:text-3xl leading-snug font-light">
            My work explores perception, memory, and presence through carefully constructed visual narratives.  
            I’m interested in how stillness and subtle motion shape the emotional experience of imagery.
          </p>

          <div className="pt-2 space-y-3">
            <p>
              <span className="font-semibold">Email:</span>{" "}
              <a
                href="mailto:your@email.com"
                className="underline underline-offset-4 hover:text-yellow-400 transition"
              >
                your@email.com
              </a>
            </p>

            <p>
              <span className="font-semibold">Instagram:</span>{" "}
              <a
                href="https://instagram.com/yourprofile"
                target="_blank"
                rel="noopener noreferrer"
                className="underline underline-offset-4 hover:text-yellow-400 transition"
              >
                Instagram
              </a>
            </p>
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
}
