import React, { useState } from "react";
import Gallery from "./Gallery";

const categories = ["Portraits", "Landscapes", "Motion", "Archive"];

export default function App() {
  const [selectedCategory, setSelectedCategory] = useState("Portraits");

  return (
    <div className="relative z-10 min-h-screen text-white font-sans bg-gray-950">
      {/* Левая колонка с логотипом и категориями, фиксированная */}
      <aside
        className="fixed top-0 left-0 h-full flex flex-col px-6 pt-6 bg-gray-950"
        style={{ width: 180 }}
      >
        <h1 className="text-4xl mb-12">Han Kim Studio</h1>

        <nav className="flex flex-col gap-3 text-lg text-gray-300 flex-grow">
          {categories.map((cat) => (
            <button
              key={cat}
              onClick={() => setSelectedCategory(cat)}
              className={`text-left transition hover:text-yellow-400 pl-3 border-l-4 ${
                selectedCategory === cat
                  ? "border-yellow-400 text-yellow-400"
                  : "border-transparent"
              }`}
            >
              {cat}
            </button>
          ))}
        </nav>
      </aside>

      {/* Основной контент — отступ слева под ширину aside */}
      <main className="ml-[180px] p-6 max-w-6xl mx-auto">
        <section id="gallery">
          <Gallery category={selectedCategory} />
        </section>
      </main>

      <footer className="p-6 text-center text-sm text-gray-400">
        © 2025 Hankim Studio. All rights reserved.
      </footer>
    </div>
  );
}
