// App.jsx
import React, { useState } from "react";
import Gallery from "./Gallery";

const categories = ["portraits", "landscapes", "motion", "contacts"];

export default function App() {
  const [selectedCategory, setSelectedCategory] = useState("portraits");

  return (
    <div className="relative z-10 min-h-screen text-white font-sans bg-gray-950">
      <header className="p-6">
        <a href="#" className="text-4xl mb-4 block hover:text-yellow-400 transition">
          hankim studio
        </a>
      </header>

      <main className="p-6 max-w-6xl mx-auto">
        <div className="flex flex-col lg:flex-row gap-12 items-start">
          {/* Боковая панель категорий */}
          <nav className="flex flex-col gap-1 text-lg text-gray-300 min-w-[150px] pt-2">
            {categories.map((cat) => (
              <button
                key={cat}
                onClick={() => setSelectedCategory(cat)}
                className={`text-left transition hover:text-yellow-400 ${
                  selectedCategory === cat ? "text-yellow-400" : ""
                }`}
              >
                {cat}
              </button>
            ))}
          </nav>

          <section id="gallery" className="flex-1">
            <h2 className="text-3xl mb-4">{selectedCategory}</h2>
            <Gallery category={selectedCategory} />
          </section>
        </div>
      </main>

      <footer className="p-6 text-center text-sm text-gray-400">
        © 2025 Hankim Studio. All rights reserved.
      </footer>
    </div>
  );
}
