import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";
import Gallery from "./Gallery";
import Contacts from "./Contacts";
import Motion from "./Motion";
import useTheme from "./hooks/useTheme";

const categories = [
  { name: "portraits", path: "/" },
  { name: "landscapes", path: "/" },
  { name: "motion", path: "/motion" },
  { name: "contacts", path: "/contacts" },
];

function Sidebar({ selectedCategory, setSelectedCategory, toggleTheme, theme }) {
  const location = useLocation();

  return (
    <aside
      className="fixed top-0 left-0 h-full flex flex-col bg-gray-950"
      style={{ width: 200, padding: "40px 24px" }}
    >
      <Link
        to="/"
        className="text-4xl mb-16 block hover:text-yellow-400 transition"
      >
        hankim studio
      </Link>

      <nav className="flex flex-col gap-1 text-lg text-gray-300 flex-grow">
        {categories.map(({ name, path }) => (
          <Link
            key={name}
            to={path}
            onClick={() => setSelectedCategory(name)}
            className={`text-left transition hover:text-yellow-400 ${
              location.pathname === path ? "text-yellow-400" : ""
            }`}
          >
            {name}
          </Link>
        ))}
      </nav>

      <button
        onClick={toggleTheme}
        className="mt-12 text-sm text-gray-400 hover:text-yellow-400 transition"
      >
        {theme === "dark" ? "‚òÄÔ∏è Light mode" : "üåô Dark mode"}
      </button>
    </aside>
  );
}

export default function App() {
  const [selectedCategory, setSelectedCategory] = React.useState("portraits");
  const { theme, toggleTheme } = useTheme();

  return (
    <Router>
      <div className="relative z-10 min-h-screen font-sans bg-white text-gray-900 dark:bg-gray-950 dark:text-white transition-colors duration-300">
        <Sidebar
          selectedCategory={selectedCategory}
          setSelectedCategory={setSelectedCategory}
          toggleTheme={toggleTheme}
          theme={theme}
        />

        <main className="ml-[200px] pt-[110px] px-8 max-w-6xl mx-auto">
          <Routes>
            <Route path="/" element={<Gallery category={selectedCategory} />} />
            <Route path="/motion" element={<Motion />} />
            <Route path="/contacts" element={<Contacts />} />
          </Routes>
        </main>

        <footer className="p-6 text-center text-sm text-gray-400 dark:text-gray-500">
          ¬© 2025 Han Kim Studio. All rights reserved.
        </footer>
      </div>
    </Router>
  );
}
