import React, { useState } from "react";
import Gallery from "./Gallery";

const categories = ["Portraits", "Landscapes", "Motion", "Archive"];

export default function App() {
  const [selectedCategory, setSelectedCategory] = useState("Portraits");

  return (
    <div className="relative z-10 min-h-screen text-white font-sans bg-gray-950">
      {/* Центрируем общий контейнер */}
      <div className="max-w-6xl mx-auto px-6">
        <header className="pt-6 pb-4">
          <h1 className="text-4xl mb-8">Han Kim Studio</h1>

          {/* Категории под логотипом, с отступом слева */}
          <nav className="flex flex-col gap-3 text-lg text-gray-300 max-w-max pl-0 sm:pl-6">
            {categories.map((cat) => (
              <button
                key={cat}
                onClick={() => setSelectedCategory(cat)}
                className={`text-left transition hover:text-yellow-400 pl-3 border-l-4 ${
                  selectedCategory === cat
                    ? "border-yellow-400 text-yellow-400"
                    : "border-transparent"
                }`}
              >
                {cat}
              </button>
            ))}
          </nav>
        </header>

        <main className="flex gap-8 mt-8">
          {/* Галерея занимает всю доступную ширину */}
          <section id="gallery" className="flex-1">
            <Gallery category={selectedCategory} />
          </section>
        </main>
      </div>

      <footer className="p-6 text-center text-sm text-gray-400">
        © 2025 Hankim Studio. All rights reserved.
      </footer>
    </div>
  );
}
