import React, { useState } from "react";

const images = [
  "/media/photo1.jpg",
  "/media/photo2.jpg",
  "/media/photo3.jpg",
  "/media/photo4.jpg",
  "/media/photo5.jpg",
  "/media/photo6.jpg",
];

export default function Gallery() {
  const [currentIndex, setCurrentIndex] = useState(null);
  const [fade, setFade] = useState(false);

  const open = (index) => {
    setCurrentIndex(index);
    setFade(true);
    playClickSound();
  };
  const close = () => setCurrentIndex(null);

  const next = () => {
    setFade(false);
    setTimeout(() => {
      setCurrentIndex((currentIndex + 1) % images.length);
      setFade(true);
      playClickSound();
    }, 150);
  };

  const prev = () => {
    setFade(false);
    setTimeout(() => {
      setCurrentIndex((currentIndex - 1 + images.length) % images.length);
      setFade(true);
      playClickSound();
    }, 150);
  };

  const handleKeyDown = (e) => {
    if (currentIndex === null) return;
    if (e.key === "ArrowRight") next();
    if (e.key === "ArrowLeft") prev();
    if (e.key === "Escape") close();
  };

  const playClickSound = () => {
    const audio = new Audio("/media/shutter.mp3");
    audio.play().catch(() => {});
  };

  React.useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  });

  return (
    <div className="font-sans" style={{ fontFamily: "Inter, sans-serif" }}>
      <div className="grid gap-6 grid-cols-[repeat(auto-fit,minmax(300px,1fr))]">
        {images.map((src, i) => (
          <img
            key={i}
            src={src}
            alt={`Work ${i + 1}`}
            onClick={() => open(i)}
            className="cursor-pointer w-full h-auto rounded shadow hover:opacity-80 transition duration-200"
          />
        ))}
      </div>

      {currentIndex !== null && (
        <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50 transition-opacity">
          <button onClick={close} className="absolute top-4 right-4 text-white text-2xl">×</button>
          <button onClick={prev} className="absolute left-4 text-white text-3xl">‹</button>
          <img
            src={images[currentIndex]}
            alt="Expanded work"
            className={`max-w-full max-h-screen object-contain transition-opacity duration-300 ease-in-out ${fade ? 'opacity-100' : 'opacity-0'}`}
            onClick={next}
          />
          <button onClick={next} className="absolute right-4 text-white text-3xl">›</button>
        </div>
      )}

      <section id="about" className="mt-16 max-w-2xl mx-auto px-4 text-lg leading-relaxed text-gray-300">
        <p>
          Работаю со светом, тишиной и ритмом. Через фотографию и видео я исследую то,
          что обычно остаётся между строк: паузы, взгляды, эхо времени. Это попытка
          зафиксировать не событие, но атмосферу.
        </p>
      </section>
    </div>
  );
}
