import React, { useState, useEffect, useRef } from 'react';
import './App.css'; // стили с градиентным фоном, сеткой, шрифтом Inter и т.п.

const images = [
  // сюда массив объектов с фото/видео для галереи
  { id: 1, type: 'image', src: '/media/photo1.jpg', alt: 'Photo 1' },
  { id: 2, type: 'image', src: '/media/photo2.jpg', alt: 'Photo 2' },
  { id: 3, type: 'video', src: '/media/video1.mp4', alt: 'Video 1' },
  // ...
];

const clickSound = new Audio('/sounds/click.mp3');

function App() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const galleryRef = useRef(null);

  // Воспроизвести звук щелчка
  const playClickSound = () => {
    clickSound.currentTime = 0;
    clickSound.play();
  };

  // Навигация вперед
  const nextItem = () => {
    playClickSound();
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  // Навигация назад
  const prevItem = () => {
    playClickSound();
    setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
  };

  // Обработка нажатия клавиш стрелок
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'ArrowRight') nextItem();
      else if (e.key === 'ArrowLeft') prevItem();
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Клик по превьюшкам
  const handleThumbnailClick = (index) => {
    if (index !== currentIndex) {
      playClickSound();
      setCurrentIndex(index);
    }
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>Hankim Studio</h1>
        {/* Убрал блок About из шапки */}
      </header>

      <main className="gallery-container" ref={galleryRef}>
        <button className="nav-button prev" onClick={prevItem} aria-label="Previous">
          ‹
        </button>

        <div className="main-media">
          {images[currentIndex].type === 'image' ? (
            <img
              src={images[currentIndex].src}
              alt={images[currentIndex].alt}
              className="main-image"
              draggable={false}
            />
          ) : (
            <video
              src={images[currentIndex].src}
              controls
              autoPlay
              muted
              className="main-video"
            />
          )}
        </div>

        <button className="nav-button next" onClick={nextItem} aria-label="Next">
          ›
        </button>

        <div className="thumbnails">
          {images.map((item, index) => (
            <div
              key={item.id}
              className={`thumbnail ${index === currentIndex ? 'active' : ''}`}
              onClick={() => handleThumbnailClick(index)}
            >
              {item.type === 'image' ? (
                <img src={item.src} alt={item.alt} draggable={false} />
              ) : (
                <video src={item.src} muted />
              )}
            </div>
          ))}
        </div>
      </main>

      {/* Блок About только внизу */}
      <footer className="about-section">
        <h2>About Hankim Studio</h2>
        <p>
          Minimalist photo & video portfolio showcasing selected works.
          Crafted with React and Tailwind CSS.
        </p>
      </footer>
    </div>
  );
}

export default App;
