import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";
import Gallery from "./Gallery";
import Contacts from "./Contacts";
import Motion from "./Motion";
import useTheme from "./hooks/useTheme";

const categories = {
  projects: {
    label: "Projects",
    subcategories: ["Portraits", "Editorial"],
  },
  works: {
    label: "Works",
    subcategories: ["Golden Age", "mmmesss"],
  },
  motion: {
    label: "Motion",
    path: "/motion",
  },
  contacts: {
    label: "Contacts",
    path: "/contacts",
  },
};


function Sidebar({ selectedCategory, setSelectedCategory, toggleTheme, theme }) {
  const location = useLocation();

  return (
    <aside
      className="fixed top-0 left-0 h-full flex flex-col bg-white dark:bg-gray-950"
      style={{ width: 200, padding: "40px 24px" }}
    >
      <Link
        to="/"
        className="text-4xl mb-16 block hover:text-yellow-400 transition text-gray-900 dark:text-white"
      >
        hankim studio
      </Link>

<nav className="flex flex-col gap-4 text-lg text-gray-700 dark:text-gray-300 flex-grow">
  {Object.entries(categories).map(([key, value]) => {
    if (value.subcategories) {
      return (
        <div key={key}>
          <div className="font-semibold mb-1">{value.label}</div>
          <ul className="ml-2 flex flex-col gap-1">
            {value.subcategories.map((sub) => {
              const subPath = `/${key}/${sub.toLowerCase().replace(/\s+/g, "-")}`;
              return (
                <Link
                  key={sub}
                  to={subPath}
                  onClick={() => setSelectedCategory(key)}
                  className={`transition hover:text-yellow-400 ${
                    location.pathname === subPath ? "text-yellow-400" : ""
                  }`}
                >
                  {sub}
                </Link>
              );
            })}
          </ul>
        </div>
      );
    } else {
      return (
        <Link
          key={key}
          to={value.path}
          onClick={() => setSelectedCategory(key)}
          className={`transition hover:text-yellow-400 ${
            location.pathname === value.path ? "text-yellow-400" : ""
          }`}
        >
          {value.label}
        </Link>
      );
    }
  })}
</nav>


      <button
        onClick={toggleTheme}
        className="mt-12 text-sm text-gray-500 dark:text-gray-400 hover:text-yellow-400 transition"
      >
        {theme === "dark" ? "‚òÄÔ∏è Light mode" : "üåô Dark mode"}
      </button>
    </aside>
  );
}

export default function App() {
  const [selectedCategory, setSelectedCategory] = React.useState("portraits");
  const { theme, toggleTheme } = useTheme();

  return (
    <Router>
      <div className="relative z-10 min-h-screen font-sans bg-white text-gray-900 dark:bg-gray-950 dark:text-white transition-colors duration-300">
        <Sidebar
          selectedCategory={selectedCategory}
          setSelectedCategory={setSelectedCategory}
          toggleTheme={toggleTheme}
          theme={theme}
        />

        <main className="ml-[200px] pt-[110px] px-8 max-w-6xl mx-auto">
          <Routes>
            <Route path="/" element={<Gallery category={selectedCategory} />} />
            <Route path="/motion" element={<Motion />} />
            <Route path="/contacts" element={<Contacts />} />
          </Routes>
        </main>

        <footer className="p-6 text-center text-sm text-gray-400 dark:text-gray-500">
          ¬© 2025 Han Kim Studio. All rights reserved.
        </footer>
      </div>
    </Router>
  );
}
