import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from "react-router-dom";
import Gallery from "./Gallery";
import Contacts from "./Contacts";
import Motion from "./Motion"; // импортируем новый компонент
import useTheme from "./hooks/useTheme"; // подключаем хук


const categories = [
  { name: "portraits", path: "/" },
  { name: "landscapes", path: "/" },
  { name: "motion", path: "/motion" },
  { name: "contacts", path: "/contacts" },

];
const { theme, toggleTheme } = useTheme();

function Sidebar({ selectedCategory, setSelectedCategory }) {
  const location = useLocation();

  return (
    <aside
      className="fixed top-0 left-0 h-full flex flex-col bg-gray-950"
      style={{ width: 200, padding: "40px 24px" }}
    >
      <Link to="/" className="text-4xl mb-16 block hover:text-yellow-400 transition">
        hankim studio
      </Link>

      <nav className="flex flex-col gap-1 text-lg text-gray-300 flex-grow">
        {categories.map(({ name, path }) => (
          <Link
            key={name}
            to={path}
            onClick={() => setSelectedCategory(name)}
            className={`text-left transition hover:text-yellow-400 ${
              (name === selectedCategory || (name === "contacts" && location.pathname === "/contacts"))
                ? "text-yellow-400"
                : ""
            }`}
          >
            {name}
          </Link>
        ))}
      </nav>
    </aside>
  );
}

export default function App() {
  const [selectedCategory, setSelectedCategory] = React.useState("portraits");

  return (
    <Router>
      <div className="relative z-10 min-h-screen text-white font-sans bg-gray-950">
        <Sidebar selectedCategory={selectedCategory} setSelectedCategory={setSelectedCategory} />

        <main className="ml-[200px] pt-[110px] px-8 max-w-6xl mx-auto">
          <Routes>
            <Route path="/" element={<Gallery category={selectedCategory} />} />
            <Route path="/contacts" element={<Contacts />} />
          </Routes>
        </main>

        <footer className="p-6 text-center text-sm text-gray-400">
          © 2025 Han Kim Studio. All rights reserved.
        </footer>
      </div>
    </Router>
  );
}
