import React from "react";
import { motion } from "framer-motion";

export default function Contacts() {
  return (
    <motion.div
      className="font-sans space-y-12"
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
    >
      {/* Большое вступление — "О себе" */}
      <p className="text-2xl md:text-3xl leading-snug font-light text-gray-900 dark:text-gray-100 max-w-4xl">
        My work explores perception, memory, and presence through carefully constructed visual narratives.  
        I’m interested in how stillness and subtle motion shape the emotional experience of imagery.
      </p>

      {/* Изображение по ширине — твоя работа */}
      <motion.img
        src="/media/avatar.jpg"
        alt="Portrait or work"
        className="w-full max-w-5xl rounded-xl shadow-xl object-cover"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.7, ease: "easeOut", delay: 0.2 }}
      />

      {/* Блок контактов */}
      <motion.div
        className="flex flex-col md:flex-row items-start gap-10 max-w-5xl"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, ease: "easeOut", delay: 0.4 }}
      >
        {/* Если хочешь — можно убрать фото тут */}
        {/* <img
          src="/media/avatar.jpg"
          alt="Han Kim"
          className="w-48 h-48 object-cover rounded-full shadow-md border-2 border-gray-300 dark:border-gray-700"
        /> */}

        <div className="text-lg md:text-xl leading-relaxed text-gray-900 dark:text-gray-100">
          <p className="mb-5 font-light">
            For project inquiries, image use, or creative collaborations — feel free to get in touch.
          </p>

          <p className="mb-3">
            <span className="font-semibold">Email:</span>{" "}
            <a
              href="mailto:your@email.com"
              className="underline underline-offset-4 hover:text-yellow-400 transition"
            >
              your@email.com
            </a>
          </p>

          <p>
            <span className="font-semibold">Instagram:</span>{" "}
            <a
              href="https://instagram.com/yourprofile"
              target="_blank"
              rel="noopener noreferrer"
              className="underline underline-offset-4 hover:text-yellow-400 transition"
            >
              Instagram
            </a>
          </p>
        </div>
      </motion.div>
    </motion.div>
  );
}
