import React, { useState } from "react";
import Gallery from "./Gallery";

const categories = ["Portraits", "Landscapes", "Motion", "Archive"];

export default function App() {
  const [selectedCategory, setSelectedCategory] = useState("Portraits");

  return (
    <div className="relative z-10 min-h-screen text-white font-sans bg-gray-950">
      {/* Блок категорий - фиксированно слева у окна */}
      <nav
        className="fixed top-24 left-0 h-full flex flex-col gap-3 text-lg text-gray-300 p-6 bg-gray-950"
        style={{ width: 160 }}
      >
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            className={`text-left transition hover:text-yellow-400 pl-3 border-l-4 ${
              selectedCategory === cat
                ? "border-yellow-400 text-yellow-400"
                : "border-transparent"
            }`}
          >
            {cat}
          </button>
        ))}
      </nav>

      {/* Центрированный основной контейнер с логотипом и галереей */}
      <div className="max-w-6xl mx-auto px-6 ml-[180px] pt-6">
        <header className="pb-8">
          <h1 className="text-4xl">Han Kim Studio</h1>
        </header>

        <main>
          <section id="gallery">
            <Gallery category={selectedCategory} />
          </section>
        </main>

        <footer className="p-6 text-center text-sm text-gray-400">
          © 2025 Hankim Studio. All rights reserved.
        </footer>
      </div>
    </div>
  );
}
